<?if($message) echo "<h1>$load_message</h1>";if($message) echo "<h1>$message</h1>";?><? if($dislpay_form){ ?>	<form action="" method="post" enctype="multipart/form-data">		<table class="add_product"> 			<tr><th>Категория:</th>				<td>					<select name="cat_id">						<? foreach($allcategories as $category){ ?>							<option value="<?= $category['id']?>" <?= ($category['id']==$product['cat_id'])?'selected':''?>  >								<?= $category['title']?>							</option>								<? } ?>					</select>				</td>			</tr>			<tr>				<th>Выберите рисунок:</th>				<td><input type="file" name="photoimg" accept="image/*"></td>			</tr>			<tr><th>Название:</th><td><input type="text" name="title" value="<?= $product['title']?>"/></td></tr>			<tr><th>Автор:</th><td><input type="text" name="author" value="<?= $product['author']?>"/></td></tr>			<tr><th>Издательство:</th><td><input type="text" name="publishing" value="<?= $product['publishing']?>"/></td></tr>			<tr><th>Год издания:</th><td><input type="text" name="pubyear" value="<?= $product['pubyear']?>"/></td></tr>			<tr><th>isbn:</th><td><input type="text" name="isbn" value="<?= $product['isbn']?>"/></td></tr>			<tr><th>Тип обложки:</th><td><input type="text" name="hardcover" value="<?= $product['hardcover']?>"/></td></tr>			<tr><th>Кол-во страниц:</th><td><input type="text" name="pagecount" value="<?= $product['pagecount']?>"/></td></tr>			<tr>				<th>Наличие:</th>				<td><input type="checkbox" name="presence" <?= ($product['presence'])?'checked':'' ?>/></td>			</tr>			<tr><th>Цена:</th><td><input type="text" name="price" value="<?= $product['price']?>"/></td></tr>			<tr><th>Краткое описание:</th><td><textarea name="about"><?=$product['about']?></textarea></td></tr>		</table>		<br/>		<input type="submit" name="save_product" value="Сохранить товар" /><br>	</form><? } ?><form><input type="<?=($dislpay_form)?'hidden':'submit'?>" name="add_product" value="Добавить товар" /><br></form><h1>Каталог товаров</h1><form action="" method="get">	<select name="category_id">		<option value="0" >- Все категории -</option>			<? foreach($allcategories as $category){ ?>				<option value="<?= $category['id']?>">					<?= $category['title']?>				</option>					<? } ?>	</select>	<input type="submit" name="select_category" value="Ok" /></form><? if($catalog): ?><table border="1" cellpadding="5" cellspacing="0">	<tr>		<th>Категория</th>		<th>Изображение</th>		<th>Название</th>		<th>Автор</th>		<th>Издательство</th>		<th>Год издания</th>		<th>isbn</th>		<th>Тип обложки</th>		<th>Кол-во страниц</th>		<th>Наличие</th>		<th>Цена</th>		<th>Редактировать</th>		<th>Удалить</th>	</tr><? foreach($catalog as $item){ ?>	<tr> 		<td><?= $item['category_title']?></td>		<td><img src="../uploads/<?=($item['image_url'])?$item['image_url']:'none.jpg'?>"/></td>		<td style="text-align:left"><?= $item['title']?></td>		<td><?= $item['author']?></td>		<td><?= $item['publishing']?></td>		<td><?= $item['pubyear']?></td>		<td><?= $item['isbn']?></td>		<td><?= $item['hardcover']?></td>		<td><?= $item['pagecount']?></td>		<td><?= ($item['presence'])?'Есть':'Нет' ?></td>		<td><?= $item['price']?></td>	<form action="" method="get">		<td><input type="hidden" name="id" value="<?= $item['id']?>">			<input type="hidden" name="image_url" value="<?= $item['image_url']?>">			<input type="submit" name="edit_product" value="Редактировать" /></td>		<td><input type="submit" name="del_product" value="Удалить" /></td>	</form>	</tr><? 	} //foreach?></table><div><form><?=$pagination?></form></div><? endif; ?>